-- ============================================================
-- 0. PRERREQUISITO: VINCULAR ATLIXCO A LA ORGANIZACIÓN
-- ============================================================
-- Idempotente: si 02_organizaciones.sql ya fue ejecutado, DO NOTHING.
-- Si el seed se ejecuta solo, este INSERT garantiza el vínculo.
INSERT INTO organizacion_municipios (organizacion_id, municipio_id)
VALUES ('00000000-0000-0000-0000-000000000001'::UUID, 1)
ON CONFLICT (organizacion_id, municipio_id) DO NOTHING;

-- Garantizar que secciones_electorales tienen municipio_id = 1
-- (se aplica si 01_catalogo_geografico.sql aún no fue ejecutado)
UPDATE secciones_electorales
  SET municipio_id = 1
  WHERE municipio_id IS NULL;

-- UUIDs fijos para referencia estable entre re-ejecuciones
-- Candidato principal
DO $$ BEGIN
  PERFORM 1 FROM candidatos WHERE id = 'a1000000-0000-0000-0000-000000000001'::UUID;
  IF NOT FOUND THEN
    INSERT INTO candidatos (
      id, organizacion_id, nombre, cargo, partido,
      municipio, color_primario, activo
    ) VALUES (
      'a1000000-0000-0000-0000-000000000001'::UUID,
      '00000000-0000-0000-0000-000000000001'::UUID,
      'Francisco García Hernández',
      'Presidente Municipal',
      'MORENA',
      'Atlixco',
      '#c9a227',
      true
    );
    RAISE NOTICE '✅ Candidato principal creado: Francisco García Hernández';
  ELSE
    RAISE NOTICE 'ℹ️  Candidato principal ya existe, omitiendo.';
  END IF;
END $$;

-- ============================================================
-- 2. CAMPAÑA DEMO
-- ============================================================
DO $$ BEGIN
  PERFORM 1 FROM campanas WHERE id = 'b2000000-0000-0000-0000-000000000001'::UUID;
  IF NOT FOUND THEN
    INSERT INTO campanas (
      id, nombre, candidato_id, organizacion_id,
      municipio_id, fecha_inicio, meta_encuestas, activa
    ) VALUES (
      'b2000000-0000-0000-0000-000000000001'::UUID,
      'Paco García 2025',
      'a1000000-0000-0000-0000-000000000001'::UUID,
      '00000000-0000-0000-0000-000000000001'::UUID,
      1,  -- Atlixco
      '2025-01-15',
      400,
      true
    );
    RAISE NOTICE '✅ Campaña demo creada: Paco García 2025';
  ELSE
    RAISE NOTICE 'ℹ️  Campaña demo ya existe, omitiendo.';
  END IF;
END $$;

